mapper.select:
SELECT a.commodity_type AS commodityType, a.area_name_original AS areaNameOriginal, a.area_name_destination AS areaNameDestination, a.end_date AS endDate, a.service, a.status, a.price, b.phone, b.real_name AS realName, b.company_name AS companyName, b.company_phone AS companyPhone, b.company_address AS companyAddress, b.type AS userType FROM t_user_commodity AS a LEFT JOIN t_user AS b ON a.user_id = b.id WHERE a.is_delete=0
AND a.commodity_type = #{commodityType}
AND a.area_name_original = #{areaNameOriginal}
AND a.area_name_destination = #{areaNameDestination}
