mapper.select:
SELECT id, username, name, type, status, phone, company_address AS companyAddress, company_name AS companyName FROM t_user WHERE is_delete = 0
AND username like CONCAT('%', #{username}, '%')
LIMIT #{index}, #{count}

mapper.update:
update
    t_user
SET status = #{status}
where id = #{id}

mapper.select:
SELECT a.id, a.username, a.real_name AS realName, a.sex, a.phone, a.company_name AS companyName, b.name AS areaName, a.company_address AS companyAddress, a.company_phone AS companyPhone, a.type, a.company_url AS companyUrl, a.remark FROM t_user AS a LEFT JOIN t_area AS b ON a.area_code = b.code WHERE a.is_delete = 0 AND a.status = 0 AND a.type != 2
AND a.username like CONCAT('%', #{username}, '%')
LIMIT #{index}, #{count}
